mapper.select:
select aid, department,worknum, name, teammate,production, class1,class2,class3,
  level, unit,publishTime,patent,certificate,schoolYear,year,status ,reason,lastTime
  from achievementInfo

       department=#{department} 
       and class2=#{class2}  
       and class3=#{class3}  
       and schoolYear=#{schoolYear}  
       and year=#{year}  
       and  worknum=#{worknum}

mapper.update:
update achievement
dptId=(select id from department where dptname=#{department}), authorId=(select id from user where worknum=#{worknum}), production=#{production}, teammate=#{teammate}, kindId=(select id from kind where class1=#{class1} and class2=#{class2} and class3=#{class3}), levelId=(select id from level where level =#{level}), unit=#{unit}, publishTime=#{publishTime}, patent=#{patent}, certificate=#{certificate}, schoolYear=#{schoolYear}, year=#{year}, status=#{status}, reason=#{reason}, lastTime=NOW()
WHERE id=#{aid}
