mapper.select:
select aid,department,worknum,name , teammate,awardUnit,content,class1,class2,class3,level,prize,bonus,awardYear ,certificate,awardTime,
schoolYear,year,status,reason,lastTime
from awardInfo

     department=#{department} 
     and class2=#{class2}  
     and class3=#{class3}  
     and level=#{level}  
     and prize=#{prize}
     and schoolYear=#{schoolYear}  
     and year=#{year}  
     and  worknum=#{worknum}

mapper.update:
update award
dtpId=(select id from department where dptname=#{department}), userId=(select id from user where worknum=#{worknum}), teammate=#{teammate}, awardUnit=#{awardUnit}, kindId=(select id from kind where class1=#{class1} and class2=#{class2} and class3=#{class3}), level=(select id from level where level =#{level}), prize=#{prize}, bonus=#{bonus}, awardYear=#{awardYear}, certificate=#{certificate}, awardTime=#{awardTime}, schoolYear=#{schoolYear}, year=#{year}, status=#{status}, reason=#{reason}, lastTime=NOW()
WHERE id=#{aid}
