mapper.update:
update construction
dptId=(select id from department where dptname=#{department}), projectNum=#{projectNum}, project=#{project}, principalId= (select id from user where worknum=#{worknum}), teammate=#{teammate}, startYear=#{startYear}, beginToEndTime=#{beginToEndTime}, sponsor=#{sponsor}, kindId=(select id from kind where class1=#{class1} and class2=#{class2} and class3=#{class3}), levelId=(select id from level where level =#{level}), testimonial=#{testimonial}, expenditure=#{expenditure}, point=#{point}, computeYear=#{computeYear}, bonus=#{bonus}, fileNumber=#{fileNumber}, isEnd=#{isEnd}, schoolyear=#{schoolyear}, year=#{year}, status=#{status}, reason=#{reason}, lastTime=NOW()
WHERE id=#{cid}
