mapper.select:
select  dptname,name,worknum,gender,birthday,enterTime,phone,techTittle,eduBgd,degree,school,major,doubleTeacher,background,tutor,permission
from userInfo



        and name = #{name}


        and worknum = #{worknum}


       and dptname = #{dptname}

mapper.update:
update user
dptId=(select id from department where dptname=#{dptname}), name=#{name}, password=#{password}, gender=#{gender}, phone=#{phone}, birthday=#{birthday}, enterTime=#{enterTime}, techTittle=#{techTittle}, eduBgd=#{eduBgd}, degree=#{degree}, school=#{school}, doubleTeacher=#{doubleTeacher}, background=#{background}, major=#{major}, tutor=#{tutor}, permission=#{permission}
WHERE worknum=#{worknum}
